svg.script:
(function() {
    const fullscreenIcon = document.getElementById("fullscreen-icon");
    const svg = document.querySelector('svg');

    if (fullscreenIcon && svg) {
        // Toggle fullscreen on click
        fullscreenIcon.addEventListener("click", function() {
            if (!document.fullscreenElement) {
                // Enter fullscreen
                svg.requestFullscreen().catch((err) => {
                    console.error("Error attempting to enable fullscreen:", err);
                });
            } else {
                // Exit fullscreen
                document.exitFullscreen();
            }
        });

        // Show/hide button based on fullscreen state
        document.addEventListener('fullscreenchange', function() {
            if (document.fullscreenElement) {
                fullscreenIcon.style.visibility = 'hidden';
            } else {
                fullscreenIcon.style.visibility = 'visible';
            }
        });
    }

    // Helper function to navigate slides
    function navigateSlides(direction) {
        const slides = document.querySelectorAll('.slide');

        // Find currently visible slide by checking computed style
        let currentIndex = -1;
        for (let i = 0; i < slides.length; i++) {
            if (window.getComputedStyle(slides[i]).display === 'block') {
                currentIndex = i;
                break;
            }
        }

        let nextIndex = currentIndex;

        if (direction === 'next' && currentIndex < slides.length - 1) {
            nextIndex = currentIndex + 1;
        } else if (direction === 'prev' && currentIndex > 0) {
            nextIndex = currentIndex - 1;
        }

        // Switch slides if index changed
        if (nextIndex !== currentIndex && nextIndex >= 0) {
            slides[currentIndex].style.display = 'none';
            slides[nextIndex].style.display = 'block';
        }
    }

    // Keyboard navigation for slides
    document.addEventListener('keydown', function(e) {
        if (e.key === 'ArrowRight') {
            navigateSlides('next');
        } else if (e.key === 'ArrowLeft') {
            navigateSlides('prev');
        }
    });

    // Touch navigation for mobile
    svg.addEventListener('click', function(e) {
        const svgRect = svg.getBoundingClientRect();
        const clickX = e.clientX - svgRect.left;
        const svgWidth = svgRect.width;
        const leftThird = svgWidth / 3;
        const rightThird = svgWidth * 2 / 3;

        if (clickX < leftThird) {
            navigateSlides('prev');
        } else if (clickX > rightThird) {
            navigateSlides('next');
        }
    });
})();
